vl_encode_description <- function(spec, field = NULL, type = NULL, aggregate = NULL, bandPosition = NULL, bin = NULL, condition = NULL, format = NULL, formatType = NULL, timeUnit = NULL, title = NULL, value = NULL, .object = NULL) {
